import { useEffect,useState } from "react";
import { connect ,useDispatch } from "react-redux";
import { Link } from "react-router-dom";
import { toast } from "react-toastify";
import {FetchBookList,RemoveBook} from "../../Redux/Action";

const Booklist = (props) => {

    useEffect(() => {
        props.loaduser();
    }, [])

    const handledelete = (code) => {
        if (window.confirm('Do you want to remove?')) {
                props.removebook(code);
                props.loaduser();
                toast.success('Book removed successfully.')
        } 
    }
    return ( 
        <div>
             <div className="card">
               <h2>Books</h2>
                <div className="card-header">
                 <div className="card-header" >
                            <Link className="btn btn-success btn-dashoard"  to={'/'} >Back To Dashboard</Link>
                         </div>
                </div>
                <div className="card-body">
                <table className="table table-bordered">
                                <thead className="bg-dark text-white">
                                    <tr>
                                        <td>ID</td>
                                        <td>Name</td>
                                        <td>ISBN</td>
                                        <td>Date</td>
                                        <td>Action 1</td>
                                        <td>Action 2</td>
                                        <td>Action 3</td>

                                    </tr>
                                </thead>
                                <tbody>
                                {
                                        props.user.booklist && props.user.booklist.books.map((item) =>
                                            <tr key={item.id}>
                                                <td>{item.id}</td>
                                                <td>{item.name} </td>
                                                <td>{item.isbn} </td>
                                                 <td>{item.created_at}</td>
                                                 <td><button type="button" className="btn btn-primary" >View More</button></td>
                                                <td><button type="button" className="btn btn-success" >Edit Book </button></td>
                                                <td><button type="button" className="btn btn-danger" onClick={() => { handledelete(item.id) }}>Delete Book</button></td>

                                             </tr>
                                        )
                                    }

                                </tbody>

                            </table>
                </div>
            </div>

        </div>
     );
}

const mapStateToProps = (state) => {
    return {
        user: state.user
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        loaduser: () => dispatch(FetchBookList()),
        removebook:(code)=>dispatch(RemoveBook(code))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Booklist);
