import { useEffect } from "react";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import { toast } from "react-toastify";
import { FetchAuthorList} from "../../Redux/Action";

const Authorlist = (props) => {
    useEffect(() => {
        props.loaduser();
        console.log(props)
    }, [])
    return ( 

      props.user.loading ? <div><h2>Loading...</h2></div> :
      props.user.errmessage ? <div><h2>{props.user.errmessage}</h2></div> :
        <div>
            <div className="card">
                <div className="card-header">
                    <h2>Authors</h2>
                </div>
                <div className="card-body">
                <table className="table table-bordered">
                                <thead className="bg-dark text-white">
                                    <tr>
                                        <td>Index</td>
                                        <td>Name</td>
                                        <td>Action 1</td>
                                        <td>Action 2</td>
                                        <td>Action 3</td>

                                    </tr>
                                </thead>
                                <tbody>
                                    {
                                        props.user.authorlist && props.user.authorlist.all_authers.map((item,index) =>
                                            <tr key={item.id}>
                                                <td>{item.id}</td>
                                                <td>{item.first_name} {item.last_name}</td>
                                                <td>{item.index}</td>

                                                <td>
                                                    {/* <Link to={'/user/edit/' + item.id} className="btn btn-primary">Edit</Link> |
                                                    <button onClick={() => { handledelete(item.id) }} className="btn btn-danger">Delete</button> */}
                                                </td>
                                            </tr>
                                        )
                                    }

                                </tbody>

                            </table>
                </div>
            </div>
        </div>
     );
}

const mapStateToProps = (state) => {
    return {
        user: state.user
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        loaduser: () => dispatch(FetchAuthorList()),
        //removeuser:(code)=>dispatch(Removeuser(code))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Authorlist);
